devicePage.template:
//! Indicates whether the UART needs a write to clear status errors 	static constexpr bool statusNeedsWrite = false;


//! Default Baud rate used if not explicitly given 	static constexpr uint32_t defaultBaudRate = $(defaultBaudRate);


//! Default buffer size for receive queue when interrupt driven 	static constexpr unsigned receiveBufferSize = $(receiveBufferSize);


//! Default buffer size for transmit queue when interrupt driven 	static constexpr unsigned transmitBufferSize = $(transmitBufferSize);


/** 	 * Get input clock frequency 	 * 	 * @return Input clock frequency as a uint32_t in Hz 	 */ 	static uint32_t getInputClockFrequency() { 	 return $(clockSource:SystemBusClock); 	}
